export default (function () {
  return {
    editor: [{
      name: "pb-editor-slate-editor-scroll",
      type: "pb-editor-slate-editor",
      slate: {
        onKeyDown: function onKeyDown(event, change, next) {
          var native = window.getSelection();

          if (native.type === "None") {
            return {
              top: 0,
              left: 0,
              width: 0,
              height: 0
            };
          }

          var range = native.getRangeAt(0);
          var pos = range.getBoundingClientRect();
          var cursorY = pos.top;
          var clientHeight = document.documentElement.clientHeight;
          var height = clientHeight - 50;

          if (cursorY > height) {
            var scrollDiff = cursorY - height;
            window.scrollTo(0, window.scrollY + scrollDiff + 20);
          }

          return next();
        }
      }
    }]
  };
});
