import { getPlugins } from "@webiny/plugins";
export default {
  type: "form-field-validator",
  name: "form-field-validator-pattern",
  validator: {
    name: "pattern",
    validate: function validate(value, validator) {
      if (!value) {
        return true;
      }

      var settings = validator.settings;
      var pattern;

      if (settings.preset === "custom") {
        pattern = settings;
      } else {
        var patternPlugin = getPlugins("form-field-validator-pattern").find(function (item) {
          return item.pattern.name === settings.preset;
        });

        if (patternPlugin) {
          pattern = patternPlugin.pattern;
        }
      }

      if (!pattern) {
        return true;
      }

      return new RegExp(pattern.regex, pattern.flags).test(value);
    }
  }
};
